22â†’fn init_tracing() {
23â†’    // ç’°å¢ƒå¤‰æ•°RUST_LOGã§ãƒ­ã‚°ãƒ¬ãƒ™ãƒ«ã‚’åˆ¶å¾¡å¯èƒ½ã€‚ä¾‹: RUST_LOG=info,sdz_api=debug
24â†’    let env_filter = tracing_subscriber::EnvFilter::try_from_default_env()
25â†’        .unwrap_or_else(|_| "info,sdz_api=debug".into());
26â†’    let use_json = std::env::var("SDZ_LOG_FORMAT")
27â†’        .ok()
28â†’        .map(|v| v.to_lowercase() == "json")
29â†’        .unwrap_or(true);
30â†’
31â†’    let builder = tracing_subscriber::fmt()
32â†’        .with_env_filter(env_filter)
33â†’        .with_target(false);
34â†’
35â†’    if use_json {
36â†’        builder.json().init();
37â†’    } else {
38â†’        builder.compact().init();
39â†’    }
40â†’}
