 31â†’
 32â†’pub fn sdz_build_router() -> Router {
 33â†’    let state = build_state();
 34â†’
 35â†’    Router::new()
 36â†’        .route("/sdz/health", get(health_handler::handle_health))
 37â†’        .route("/sdz/users/me", get(user_handler::handle_get_me))
 38â†’        .route(
 39â†’            "/sdz/spots",
 40â†’            axum::routing::post(spot_handler::handle_create_spot),
 41â†’        )
 42â†’        .route(
 43â†’            "/sdz/spots/upload-url",
 44â†’            axum::routing::post(spot_handler::handle_create_upload_url),
 45â†’        )
 46â†’        .route("/sdz/spots", get(spot_handler::handle_list_spots))
 47â†’        .route(
 48â†’            "/sdz/spots/{spot_id}",
 49â†’            get(spot_handler::handle_get_spot).patch(spot_handler::handle_update_spot),
 50â†’        )
 51â†’        .route(
 52â†’            "/sdz/mylist",
 53â†’            get(mylist_handler::handle_list_mylist).post(mylist_handler::handle_add_mylist),
 54â†’        )
 55â†’        .route(
 56â†’            "/sdz/mylist/{spot_id}",
 57â†’            delete(mylist_handler::handle_remove_mylist),
 58â†’        )
 59â†’        .with_state(state)
 60â†’        .layer(PropagateRequestIdLayer::x_request_id())
 61â†’        .layer(SetRequestIdLayer::x_request_id(MakeRequestUuid))
 62â†’        .layer(cors_layer())
 63â†’        .layer(
 64â†’            TraceLayer::new_for_http()
 65â†’                .make_span_with(|request: &Request<Body>| {
 66â†’                    let request_id = request
 67â†’                        .extensions()
 68â†’                        .get::<tower_http::request_id::RequestId>()
 69â†’                        .and_then(|id| id.header_value().to_str().ok())
 70â†’                        .unwrap_or("-");
 71â†’                    tracing::info_span!(
 72â†’                        "http_request",
 73â†’                        event_code = "SDZ-API-1001",
 74â†’                        component = "presentation",
 75â†’                        method = %request.method(),
 76â†’                        uri = %request.uri(),
 77â†’                        request_id = %request_id
 78â†’                    )
 79â†’                })
