119â†’    if let Ok(origins) = std::env::var("SDZ_CORS_ALLOWED_ORIGINS") {
120â†’        let list: Vec<HeaderValue> = origins
121â†’            .split(',')
122â†’            .filter_map(|s| {
123â†’                let trimmed = s.trim();
124â†’                if trimmed.is_empty() {
125â†’                    None
126â†’                } else {
127â†’                    HeaderValue::from_str(trimmed).ok()
128â†’                }
129â†’            })
130â†’            .collect();
