118â†’static HTTP_CLIENT: Lazy<Client> = Lazy::new(|| Client::builder().build().expect("client"));
119â†’
120â†’async fn fetch_jwks() -> Result<HashMap<String, String>, SdzApiError> {
121â†’    // Firebase/Identity Platformã®å…¬é–‹éµï¼ˆkid->x509 certï¼‰ã‚¨ãƒ³ãƒ‰ãƒã‚¤ãƒ³ãƒˆ
122â†’    // å‚è€ƒ: https://firebase.google.com/docs/auth/admin/verify-id-tokens#retrieve_public_keys
123â†’    let jwks_url =
124â†’        "https://www.googleapis.com/robot/v1/metadata/x509/securetoken@system.gserviceaccount.com";
125â†’    let resp = HTTP_CLIENT
126â†’        .get(jwks_url)
127â†’        .send()
128â†’        .await
129â†’        .map_err(|e| {
130â†’            tracing::error!("Failed to fetch JWKS: {:?}", e);
131â†’            SdzApiError::Internal
132â†’        })?
133â†’        .json::<HashMap<String, String>>()
134â†’        .await
135â†’        .map_err(|e| {
136â†’            tracing::error!("Failed to parse JWKS: {:?}", e);
137â†’            SdzApiError::Internal
138â†’        })?;
139â†’    Ok(resp)
140â†’}
141â†’
142â†’async fn verify_firebase_token(token: &str) -> Result<FirebaseClaims, SdzApiError> {
143â†’    let header = decode_header(token).map_err(|e| {
144â†’        tracing::error!("Failed to decode JWT header: {:?}", e);
145â†’        SdzApiError::Unauthorized
146â†’    })?;
147â†’    let kid = header.kid.ok_or_else(|| {
148â†’        tracing::error!("JWT header missing kid");
149â†’        SdzApiError::Unauthorized
150â†’    })?;
151â†’
152â†’    let jwks = fetch_jwks().await?;
153â†’    let pem = jwks.get(&kid).ok_or_else(|| {
154â†’        tracing::error!("kid not found in JWKS: {}", kid);
155â†’        SdzApiError::Unauthorized
156â†’    })?;
157â†’    let decoding_key = DecodingKey::from_rsa_pem(pem.as_bytes()).map_err(|e| {
158â†’        tracing::error!("Failed to build decoding key: {:?}", e);
159â†’        SdzApiError::Unauthorized
160â†’    })?;
161â†’
162â†’    let project_id = std::env::var("SDZ_AUTH_PROJECT_ID").map_err(|e| {
163â†’        tracing::error!("SDZ_AUTH_PROJECT_ID not set: {:?}", e);
164â†’        SdzApiError::Internal
165â†’    })?;
166â†’    let issuer = format!("https://securetoken.google.com/{}", project_id);
167â†’
168â†’    let mut validation = Validation::new(Algorithm::RS256);
169â†’    validation.set_issuer(&[issuer]);
170â†’    validation.set_audience(&[project_id]);
171â†’    validation.validate_exp = true;
172â†’
173â†’    let data = decode::<FirebaseClaims>(token, &decoding_key, &validation).map_err(|e| {
174â†’        tracing::error!("JWT decode/validate error: {:?}", e);
175â†’        SdzApiError::Unauthorized
176â†’    })?;
177â†’
178â†’    Ok(data.claims)
179â†’}
