 45â†’    async fn fetch_access_token(&self) -> Result<String, SdzApiError> {
 46â†’        if let Ok(token) = std::env::var("SDZ_STORAGE_SIGNING_TOKEN") {
 47â†’            return Ok(token);
 48â†’        }
 49â†’        if let Ok(token) = std::env::var("SDZ_FIRESTORE_TOKEN") {
 50â†’            return Ok(token);
 51â†’        }
 52â†’
 53â†’        let metadata_url = "http://metadata.google.internal/computeMetadata/v1/instance/service-accounts/default/token";
 54â†’        let resp = self
 55â†’            .http
 56â†’            .get(metadata_url)
 57â†’            .header("Metadata-Flavor", "Google")
 58â†’            .send()
 59â†’            .await
 60â†’            .map_err(|e| {
 61â†’                tracing::error!("Failed to fetch metadata token: {:?}", e);
 62â†’                SdzApiError::Internal
 63â†’            })?;
 64â†’
 65â†’        if !resp.status().is_success() {
 66â†’            let body = resp.text().await.unwrap_or_default();
 67â†’            tracing::error!("Metadata token error: {}", body);
 68â†’            return Err(SdzApiError::Internal);
 69â†’        }
 70â†’
 71â†’        let token = resp.json::<SdzMetadataToken>().await.map_err(|e| {
 72â†’            tracing::error!("Failed to parse metadata token: {:?}", e);
 73â†’            SdzApiError::Internal
 74â†’        })?;
 75â†’
 76â†’        Ok(token.access_token)
