 79â†’    async fn sign_blob(&self, access_token: &str, payload: &str) -> Result<String, SdzApiError> {
 80â†’        let url = format!(
 81â†’            "https://iamcredentials.googleapis.com/v1/projects/-/serviceAccounts/{}:signBlob",
 82â†’            self.sdz_service_account_email
 83â†’        );
 84â†’
 85â†’        let req = SdzSignBlobRequest {
 86â†’            payload: general_purpose::STANDARD.encode(payload.as_bytes()),
 87â†’        };
 88â†’
 89â†’        let resp = self
 90â†’            .http
 91â†’            .post(url)
 92â†’            .bearer_auth(access_token)
 93â†’            .json(&req)
 94â†’            .send()
 95â†’            .await
 96â†’            .map_err(|e| {
 97â†’                tracing::error!("signBlob request error: {:?}", e);
 98â†’                SdzApiError::Internal
 99â†’            })?;
100â†’
101â†’        if !resp.status().is_success() {
102â†’            let body = resp.text().await.unwrap_or_default();
103â†’            tracing::error!("signBlob error: {}", body);
104â†’            return Err(SdzApiError::Internal);
105â†’        }
106â†’
107â†’        let signed = resp.json::<SdzSignBlobResponse>().await.map_err(|e| {
108â†’            tracing::error!("Failed to parse signBlob response: {:?}", e);
109â†’            SdzApiError::Internal
110â†’        })?;
111â†’
112â†’        let signature_bytes = general_purpose::STANDARD
113â†’            .decode(signed.signed_blob.as_bytes())
114â†’            .map_err(|e| {
115â†’                tracing::error!("Failed to decode signedBlob: {:?}", e);
116â†’                SdzApiError::Internal
117â†’            })?;
118â†’
119â†’        Ok(signature_bytes.encode_hex())
120â†’    }
