122â†’    fn build_signed_url(
123â†’        &self,
124â†’        request: &SdzUploadUrlRequest,
125â†’        now: DateTime<Utc>,
126â†’        signature: &str,
127â†’    ) -> String {
128â†’        let timestamp = now.format("%Y%m%dT%H%M%SZ").to_string();
129â†’        let timestamp_query = timestamp.clone();
130â†’        let datestamp = now.format("%Y%m%d").to_string();
131â†’        let scope = format!("{}/auto/storage/goog4_request", datestamp);
132â†’        let credential = format!("{}/{}", self.sdz_service_account_email, scope);
133â†’
134â†’        let mut query_params = vec![
135â†’            (
136â†’                "X-Goog-Algorithm".to_string(),
137â†’                SDZ_GCS_ALGORITHM.to_string(),
138â†’            ),
139â†’            ("X-Goog-Credential".to_string(), credential),
140â†’            ("X-Goog-Date".to_string(), timestamp_query),
141â†’            (
142â†’                "X-Goog-Expires".to_string(),
143â†’                self.sdz_expires_in.to_string(),
144â†’            ),
145â†’            (
146â†’                "X-Goog-SignedHeaders".to_string(),
147â†’                "content-type;host".to_string(),
148â†’            ),
149â†’        ];
150â†’
151â†’        query_params.sort();
152â†’        let canonical_query = sdz_build_query(&query_params);
153â†’        let canonical_uri = sdz_build_canonical_uri(&self.sdz_bucket, &request.sdz_object_name);
154â†’
155â†’        format!(
156â†’            "https://{}{}?{}&X-Goog-Signature={}",
157â†’            SDZ_STORAGE_HOST, canonical_uri, canonical_query, signature
158â†’        )
159â†’    }
