161â†’    fn build_object_url(&self, object_name: &str) -> String {
162â†’        let encoded_path = sdz_encode_path(object_name);
163â†’        format!(
164â†’            "https://{}/{}/{}",
165â†’            SDZ_STORAGE_HOST, self.sdz_bucket, encoded_path
166â†’        )
167â†’    }
